import React, { useEffect, useState } from 'react';
import './App.css';     
import PokeCard from './PokeCard'
function App(){

     var[pokedata,setPokedata]=useState([])
     var[pokeimg,setPokeimg]=useState([])

    const getpokemon=async()=>{
        const response= await fetch("https://pokeapi.co/api/v2/pokemon/")
        const data= await response.json(); 
        setPokedata(()=>{
            return [...data.results]
        })    
    }
    useEffect(()=>{
        getpokemon();
    },[])


    console.log(pokedata);
    console.log(pokeimg)

    return(
    <>
    <h1 className="heading">POKE<span style={{color:'#F31313'}}>DEX</span></h1><br/>
    <div className="searchBar_container">
        <input className="searchBar" placeholder="search pokemon"/>
        <i className="fa fa-search" aria-hidden="true"></i>
    </div>
    <div className="cardContainer">
       {
           pokedata.map((pokemon,index)=>{
               return <PokeCard name={pokemon.name} img={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${index+1}.png`}/>
           })
       }
    </div>
 </>)
}
export default App;
