import React, { useEffect, useState } from 'react';
import './App.css';     
import PokeCard from './PokeCard'


function App(){
    const noOfPokemon=50;

    var[pokedata,setPokedata]=useState([]);
    var[loading,setloading]=useState(true);

    const getpokemon=async(id)=>{

        const response= await fetch(`https://pokeapi.co/api/v2/pokemon/${id}`);
        const data= await response.json(); 
        console.log(data.types[0].type)
        setPokedata((pre)=>{
            return [...pre,[data.name,data.sprites.front_default,data.types]]
        })
    }

    useEffect(async()=>{
        for(let i=1;i<=noOfPokemon;i++)
        {
            await getpokemon(i);
            setloading(false)
        }
    },[])

    return(
    <>
    <h1 className="heading">POKE<span style={{color:'#F31313'}}>DEX</span></h1><br/>
    {
        loading?<h1>loading...</h1>:<><div className="searchBar_container">
        <input className="searchBar" placeholder="search pokemon" 
        />
        <i className="fa fa-search" aria-hidden="true"></i>
    </div>
    <div className="cardContainer">
       {
           pokedata.map((pokemon,id)=>{
               return <PokeCard name={pokemon[0]} img={pokemon[1]} key={id} />
           })
       }
    </div></>
    }
 </>)
}
export default App;
