import React from 'react';
import Cards from './Cards'
import './App.css'
import Data from './Data'

    let n=0;
    for(let i=0;i<Data.length;i++)
    {n++;}

    function displaCards(category)
    {
        if(category==='all')
        {
            return(
                 Data.map((item)=>{
                    return(
                    <Cards
                    key={item.id}
                    src={item.imgSrc}  
                    title={item.title} 
                    link={item.watchLink} / >)
                    })
                    );

        }
    }


function App(){
    return(
    <>
    <h1 className="heading">Top {n} <span style={{color:'#e63946'}}>NETFLIX</span> Series</h1>
    <div className="line"></div>

    <div className="btnContainer">
        <button className="allbtn" onClick={displaCards('all')}>All</button>
        <button className="amazonbtn" onClick={displaCards('amazon')}>AMAZON</button>
        <button className="netflixbtn" onClick={displaCards('netflix')}>NETFLIX</button>
    </div>
    <div className="cardContainer">
        {Data.map((item)=>{
            return(
        <Cards
        key={item.id}
        src={item.imgSrc}  
        title={item.title} 
        link={item.watchLink} / >)
        })}
 </div>
 </>)
}
export default App;
