import React, { useEffect, useState } from 'react';
import "./App.css";     
import PokeCard from './components/PokeCard'
import PokemonModal from "./components/PokemonModal";

function App(){
    const noOfPokemon=50;
    var[data,setdata]=useState([])
    var[pokedata,setPokedata]=useState(data);
    var[loading,setloading]=useState(true);
    var[modalVisibility,setmodalVisibility]=useState(false)
    const getpokemon=async(id)=>{

        const response= await fetch(`https://pokeapi.co/api/v2/pokemon/${id}`);
        const data= await response.json(); 
        setdata((pre)=>{
            return [...pre,[data.name,data.sprites.front_default,data.types[0].type.name]]
        })
    }

    const searchpokemon=async(e)=>{
        let value=e.target.value;

        if(value===''){
            setPokedata(data);
        }
        else{
            let newItems=data.filter((item)=>{
                return item[0].indexOf(value)>=0;
            })
            setPokedata(newItems)
        }
    }
    const getMoreInfo=(delindex)=>{
        setmodalVisibility(()=> true);
    }

    const closeModal=()=>{
        setmodalVisibility(false)
    }



    useEffect(async()=>{
        for(let i=1;i<=noOfPokemon;i++)
        {
            await getpokemon(i);
            setloading(false);
        }
    },[])
    useEffect(()=>{
        setPokedata(data)
    },[data])

    return(
    <>
    <h1 className="heading">POKE<span style={{color:'#F31313'}}>DEX</span></h1><br/>
    {
        loading?<h1>Loading...</h1>:<><div className="searchBar_container">
        <input className="searchBar" placeholder="search pokemon" 
        onChange={searchpokemon}       />
        <i className="fa fa-search" aria-hidden="true"></i>
    </div>

    <div className="mainsection"> 
    {modalVisibility?<PokemonModal closeModal={closeModal} />:null}
        <div className="cardContainer">
        {
            pokedata.map((pokemon,index)=>{
                return <PokeCard getMoreInfo={getMoreInfo}
                name={pokemon[0]} 
                img={pokemon[1]} 
                id={index}
                key={index} 
                type={pokemon[2]}/>
            })
        }
        </div>
    </div></>
    }
 </>)
}
export default App;
