import React from 'react'

export default function PokemonStats(props) {

 console.log(props)



    return (
        <div className="stats">
                 <h2 className="stats_headng" style={{marginBottom:'1rem'}}> STATS </h2>   
                  {props.stats.map((item)=>{

                      if(item.stat.name!='special-attack' && item.stat.name!='special-defense'){
                        return (
                            <div style={{textAlign:'right',backgroundColor:'green',width:'77%',margin:'auto',marginTop:'0.3rem'}}>
                            <h4 style={{display:'inline-block',fontSize:'1.5rem'}}>{item.stat.name}</h4>
                            <div className='progress_container' style={{display:'inline-block',marginLeft:'0'}}>
                                <div className="progress"  style={{width:`${item.base_stat}px`}}></div>
                            </div>
                            <span>{item.base_stat}/100</span>
                        </div >)
                      }
                    
                  })
                  }        
        </div>
    )
}
