import React, { useEffect, useState } from 'react';
import './App.css';     
import PokeCard from './PokeCard'


function App(){

     var[pokedata,setPokedata]=useState([]);

    const getpokemon=async()=>{
        const response= await fetch("https://pokeapi.co/api/v2/pokemon/");
        const data= await response.json(); 

        await (data.results).forEach(element => {
                    let name=element.name;
                    setPokedata((pre)=>{
                        return[...pre,name]
                    })
                });
            console.log(pokedata)
         getpokeimgs();
    }
    useEffect(()=>{
        getpokemon();
    },[])

    const getpokeimgs=async()=>{
        console.log('came here')
        for(let i=0;i<pokedata.length;i++)
        {
            let response= await fetch(`https://pokeapi.co/api/v2/pokemon/${pokedata[i].name}`)
            let data= await response.json()
            console.log(data);
        }
    }
  // console.log(pokedata);

    const searchpokemon=(e)=>{

        var value=(e.target.value);
        console.log(value);
        if(value!=='')
        {
            let newItems= pokedata.filter((item)=>{
            return (item.name.indexOf(value)>=0)
            })
            console.log(newItems);
            setPokedata(newItems);
        }

    }

    return(
    <>
    <h1 className="heading">POKE<span style={{color:'#F31313'}}>DEX</span></h1><br/>
    <div className="searchBar_container">
        <input className="searchBar" placeholder="search pokemon" 
        onChange={searchpokemon}/>
        <i className="fa fa-search" aria-hidden="true"></i>
    </div>
    <div className="cardContainer">
       {
           pokedata.map((pokemon,index)=>{
               return <PokeCard name={pokemon.name} img={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${index+1}.png`}/>
           })
       }
    </div>
 </>)
}
export default App;
